<?php

function cpt_matches() {
	register_post_type( 'matches',
	     array(
	        'labels' => array(
	        'name' => __( 'Matches', 'indivio' ),
	        'singular_name' => __( 'Match', 'indivio' ),
	 		'all_items'           => __( 'Matches', 'indivio' ),
	 		'view_item'           => __( 'View match', 'indivio' ),
	 		'add_new_item'        => __( 'Add new match', 'indivio' ),
	 		'add_new'             => __( 'Add match', 'indivio' ),
	 		'edit_item'           => __( 'Edit match', 'indivio' ),
	 		'update_item'         => __( 'Update match', 'indivio' ),
	 		'search_items'        => __( 'Search match', 'indivio' ),
	 		'not_found'           => __( 'Match not found', 'indivio' ),
	    ),
	        'public' => true,
	        'has_archive' => false,
	        'publicly_queryable'  => false,
			'show_in_menu' => 'everblaze-panel'
	    )
	);
	register_post_type( 'opponents',
	     array(
	        'labels' => array(
	        'name' => __( 'Opponents', 'indivio' ),
	        'singular_name' => __( 'Opponent', 'indivio' ),
	        'all_items'           => __( 'Opponents', 'indivio' ),
	        'view_item'           => __( 'View opponent', 'indivio' ),
	        'add_new_item'        => __( 'Add new opponent', 'indivio' ),
	        'add_new'             => __( 'Add opponent', 'indivio' ),
	        'edit_item'           => __( 'Edit opponent', 'indivio' ),
	        'update_item'         => __( 'Update opponent', 'indivio' ),
	        'search_items'        => __( 'Search opponent', 'indivio' ),
	        'not_found'           => __( 'Opponent not found', 'indivio' ),
	    ),
	        'public' => true,
	        'has_archive' => false,
	        'publicly_queryable'  => false,
	        'show_in_menu' => 'everblaze-panel'
	    )
	);
}
add_action( 'init', 'cpt_matches' );

